export const MessageBox = forwardRef<HTMLDivElement, MessageBoxProps>(
  (
    {
      open,
      onOpenChange,
      type = "Confirm",
      title,
      titleText,
      children,
      actions,
      emphasizedAction,
      onAction,
      className,
      state,
      ...props
    },
    ref
  ) => {
    const resolvedType = useMemo(() => {
      if (state) {
        switch (state) {
          case "Success":
            return "Success";
          case "Warning":
            return "Warning";
          case "Error":
            return "Error";
          case "Information":
            return "Information";
          default:
            return type;
        }
      }
      return type;
    }, [state, type]);

    const resolvedTitle = title || titleText;
    const resolvedActions = actions || defaultActions[resolvedType];
    const borderColor = messageBoxColors[resolvedType];

    const handleAction = (action: MessageBoxAction) => {
      onAction?.(action);
      onOpenChange?.(false);
    };

    // Handle Escape key
    useEffect(() => {
      const handleKeyDown = (event: KeyboardEvent) => {
        if (event.key === "Escape" && open) {
          const cancelAction = resolvedActions.find((action) =>
            ["Cancel", "No", "Abort"].includes(action)
          );
          if (cancelAction) handleAction(cancelAction);
          else onOpenChange?.(false);
        }
      };

      if (open) {
        document.addEventListener("keydown", handleKeyDown);
        return () => document.removeEventListener("keydown", handleKeyDown);
      }
    }, [open, resolvedActions, onOpenChange, onAction]);

    const renderActions = () => {
      return resolvedActions.map((action) => {
        const isEmphasized =
          emphasizedAction === action ||
          (emphasizedAction === undefined && ["OK", "Yes"].includes(action));

        if (["Cancel", "No", "Abort"].includes(action)) {
          return (
            <AlertDialogCancel
              key={action}
              onClick={() => handleAction(action)}
              className="text-primary shadow-none border-0 font-bold"
            >
              {action === "Cancel" ? "Abbrechen" : action}
            </AlertDialogCancel>
          );
        }

        return (
          <AlertDialogAction
            key={action}
            onClick={() => handleAction(action)}
            className={cn(
              isEmphasized
                ? "font-bold px-5 py-2"
                : "bg-transparent text-primary font-bold border-0 h-auto hover:bg-transparent"
            )}
          >
            {action}
          </AlertDialogAction>
        );
      });
    };

    return (
      <AlertDialog open={open} onOpenChange={onOpenChange}>
        <AlertDialogContent
          ref={ref}
          className={cn(
            "max-w-lg border border-gray-300 shadow-lg p-0 gap-0 rounded-[12px]",
            className
          )}
          {...props}
        >
          <AlertDialogHeader
            className={cn("px-4 py-[10px] space-y-0 border-b", borderColor)}
            style={{
              boxShadow:
                "0 .125rem .125rem 0 rgba(34,53,72,.05), inset 0 -.0625rem 0 0 #d9d9d9",
            }}
          >
            {resolvedTitle && (
              <AlertDialogTitle className="flex items-center gap-2 text-base font-bold ">
                {ICON_MAP[resolvedType]}
                {resolvedTitle}
              </AlertDialogTitle>
            )}
          </AlertDialogHeader>
          {children && (
            <AlertDialogDescription className="text-left text-foreground text-sm px-5 py-3">
              {children}
            </AlertDialogDescription>
          )}
          <AlertDialogFooter className="flex-row justify-end gap-2 py-1 px-4 bg-transparent space-x-0 space-y-0 border-t border-[#d9d9d9]">
            {renderActions()}
          </AlertDialogFooter>
        </AlertDialogContent>
      </AlertDialog>
    );
  }
);

MessageBox.displayName = "MessageBox";

export { MessageBox as default };

