import { type ReactNode } from "react";

import { BrandHeader } from "@/components/brand-header";

import { cn } from "@/lib/utils";

import "@/app/globals.css";
import "@/app/tokens.css";

export default function ShellLayout({
  children,
}: Readonly<{
  children: ReactNode;
}>) {
  return (
    <html
      lang="en"
      className={cn("font-sans bg-background text-foreground")}
    >
      <body>
        <BrandHeader />
        <main className="mt-16 flex w-full justify-center">
          <div className="container">{children}</div>
        </main>
      </body>
    </html>
  );
}

export default function DashboardPage() {
  return (
    <div className="flex min-h-[90vh] flex-col items-center justify-center gap-4 rounded-md bg-background">
      <div className="min-w-32 text-muted-foreground">
        To get started, click the open in v0 button!
      </div>
    </div>
  );
}

import type { Config } from "tailwindcss";

const config: Config = {
  content: [
    "./pages/**/*.{ts,tsx,js,jsx,mdx}",
    "./components/**/*.{ts,tsx,js,jsx,mdx}",
    "./app/**/*.{ts,tsx,js,jsx,mdx}",
    "*.{ts,tsx,js,jsx,mdx}",
  ],
  theme: {
    extend: {
      colors: {
        background: "var(--background)",
        foreground: "var(--foreground)",

        card: "var(--card)",
        "card-foreground": "var(--card-foreground)",

        popover: "var(--popover)",
        "popover-foreground": "var(--popover-foreground)",

        primary: "var(--primary)",
        "primary-foreground": "var(--primary-foreground)",

        secondary: "var(--secondary)",
        "secondary-foreground": "var(--secondary-foreground)",

        muted: "var(--muted)",
        "muted-foreground": "var(--muted-foreground)",

        accent: "var(--accent)",
        "accent-foreground": "var(--accent-foreground)",

        destructive: "var(--destructive)",
        "destructive-foreground": "var(--destructive-foreground)",

        border: "var(--border)",
        input: "var(--input)",
        ring: "var(--ring)",

        chart: {
          1: "var(--chart-1)",
          2: "var(--chart-2)",
          3: "var(--chart-3)",
          4: "var(--chart-4)",
          5: "var(--chart-5)",
        },

        sidebar: {
          DEFAULT: "var(--sidebar)",
          foreground: "var(--sidebar-foreground)",
          primary: "var(--sidebar-primary)",
          "primary-foreground": "var(--sidebar-primary-foreground)",
          accent: "var(--sidebar-accent)",
          "accent-foreground": "var(--sidebar-accent-foreground)",
          border: "var(--sidebar-border)",
          ring: "var(--sidebar-ring)",
        },
      },
      borderRadius: {
        lg: "var(--radius)",
        md: "calc(var(--radius) - 2px)",
        sm: "calc(var(--radius) - 4px)",
      },
      boxShadow: {
        "2xs": "var(--shadow-2xs)",
        xs: "var(--shadow-xs)",
        sm: "var(--shadow-sm)",
        DEFAULT: "var(--shadow)",
        md: "var(--shadow-md)",
        lg: "var(--shadow-lg)",
        xl: "var(--shadow-xl)",
        "2xl": "var(--shadow-2xl)",
      },
      fontFamily: {
        serif: ["var(--font-serif)", "sans-serif"],
        sans: ["var(--font-sans)", "sans-serif"],
        mono: ["var(--font-mono)", "monospace"],
      },
      keyframes: {
        "fade-in-scale": {
          "0%": { opacity: "0", transform: "scale(0.95)" },
          "100%": { opacity: "1", transform: "scale(1)" },
        },
      },
      animation: {
        "fade-in-scale": "fade-in-scale 0.3s ease-out",
      },
    },
  },
  plugins: [require("tailwindcss-animate")],
};
export default config;

/**
 * SAP Fiori 3 Design Tokens
 * Mapped to standard CSS custom property naming conventions
 */
@import "@sap-theming/theming-base-content/content/Base/baseLib/sap_horizon/css_variables.css";

/* Font Face Declarations */
@font-face {
  font-family: 'SAP72';
  font-style: normal;
  font-weight: 400;
  src: url("https://ui5.sap.com/sdk/resources/sap/ui/core/themes/sap_fiori_3/fonts/72-Regular.woff2") format('woff2');
}

@font-face {
  font-family: 'SAP72';
  font-style: normal;
  font-style: bold;
  font-weight: 800;
  src: url("https://ui5.sap.com/sdk/resources/sap/ui/core/themes/sap_fiori_3/fonts/72-Bold.woff2") format('woff2');
}

@font-face {
  font-family: 'SAP72';
  font-style: normal;
  font-style: bold;
  font-weight: 800;
  src: url('https://ui5.sap.com/resources/sap/ui/core/themes/sap_horizon/fonts/72-Black.woff2') format('woff2');
}

:root {
  /* Base Colors - SAP Fiori 3 Light Theme */
  --background: #fff;
  --foreground: #131e29;
  --card: #ffffff;
  --card-foreground: #131e29;
  --popover: #ffffff;
  --popover-foreground: #131e29;
  --primary: #0070f2;
  --primary-foreground: #ffffff;
  --secondary: #f5f6f7;
  --secondary-foreground: #131e29;
  --muted: #f4f4f4;
  --muted-foreground: rgba(19, 30, 41, 0.6);

  --accent: #ebf5fe;
  --accent-foreground: var(--sapHighlightColor);

  --destructive: var(--sapField_InvalidColor);
  --destructive-foreground: var(--sapErrorColor);

  --border: var(--sapButton_Track_BorderColor);
  /* --input: var(--sapField_Background); */
  --input: var(--sapField_BorderColor);
  /* --input: #ccc; */
  --ring: var(--sapField_Active_BorderColor);

  /* Chart Colors - SAP Fiori 3 Palette */
  --chart-1: var(--sapChart_OrderedColor_1);
  --chart-2: var(--sapChart_OrderedColor_3);
  --chart-3: var(--sapChart_OrderedColor_2);
  --chart-4: var(--sapChart_OrderedColor_4);
  --chart-5: var(--sapChart_OrderedColor_5);

  /* Sidebar/Shell Colors */
  --sidebar: #fff;
  --sidebar-foreground: #ffffff;
  --sidebar-foreground: var(--sapList_TextColor);
  --sidebar-primary: #d1e8ff;
  --sidebar-primary-foreground: #354a5f;
  --sidebar-accent: var(--hoverBackgroundColor); /* hover color */
  --sidebar-accent: #eaecee; /* hover color */
  --sidebar-accent-foreground: var(--sapList_TextColor);
  --sidebar-border: #7996b4;
  --sidebar-ring: #d1e8ff;

  /* Typography */
  --font-sans: 'SAP72', ui-sans-serif, system-ui, -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, 'Helvetica Neue', Arial, sans-serif;
  --font-serif: var(--font-sans);
  --font-mono: '72Mono', ui-monospace, SFMono-Regular, Menlo, Monaco, Consolas, "Liberation Mono", "Courier New", monospace;

  /* Border Radius - SAP uses smaller radii */
  --radius: var(--sapButton_BorderCornerRadius);

  /* Shadows - SAP Fiori 3 Shadow System */
  /* --shadow-2xs: 0 0 0 0.0625rem rgba(0,0,0,0.05);
  --shadow-xs: 0 0 0 0.0625rem rgba(0,0,0,0.1), 0 0.125rem 0.5rem 0 rgba(0,0,0,0.1);
  --shadow-sm: 0 0 0 0.0625rem rgba(0,0,0,0.1), 0 0.125rem 0.5rem 0 rgba(0,0,0,0.1);
  --shadow: 0 0 0 0.0625rem rgba(0,0,0,0.1), 0 0.125rem 0.5rem 0 rgba(0,0,0,0.1);
  --shadow-md: 0 0 0 0.0625rem rgba(0,0,0,0.42), 0 0.125rem 0.5rem 0 rgba(0,0,0,0.3);
  --shadow-lg: 0 0 0 0.0625rem rgba(0,0,0,0.42), 0 0.625rem 1.875rem 0 rgba(0,0,0,0.3);
  --shadow-xl: 0 0 0 0.0625rem rgba(0,0,0,0.42), 0 1.25rem 5rem 0 rgba(0,0,0,0.3);
  --shadow-2xl: 0 0.125rem 0.125rem 0 rgba(0,0,0,0.05), inset 0 -0.0625rem 0 0 #d9d9d9; */
  --shadow-2xs: var(--sapContent_Lite_Shadow);
  --shadow-xs: var(--sapContent_Lite_Shadow);
  --shadow-sm: var(--sapContent_Shadow0);
  --shadow: var(--sapContent_Shadow0);
  --shadow-md: var(--sapContent_Shadow1);
  --shadow-lg: var(--sapContent_Shadow2);
  --shadow-xl: var(--sapContent_Shadow3);
  --shadow-2xl: var(--sapContent_Shadow3);

  /* Typography */
  --tracking-normal: 0em;
  --spacing: 0.25rem;

  /* SAP Specific States */
  --success: #107e3e;
  --success-foreground: #ffffff;
  --warning: #df6e0c;
  --warning-foreground: #ffffff;
  --information: #0a6ed1;
  --information-foreground: #ffffff;
  --neutral: #6a6d70;
  --neutral-foreground: #ffffff;

  /* SAP State Backgrounds */
  --success-background: #f1fdf6;
  --warning-background: #fef7f1;
  --error-background: #ffebeb;
  --information-background: #f5faff;
  --neutral-background: #f4f4f4;

  --sapPageHeader_BorderColor: #d9d9d9;
}

.dark {
  /* Base Colors - SAP Fiori 3 Dark Theme (Official) */
  --background: #1c2228;
  --foreground: #fafafa;
  --card: #29313a;
  --card-foreground: #fafafa;
  --popover: #29313a;
  --popover-foreground: #fafafa;
  --primary: #91c8f6;
  --primary-foreground: #29313a;
  --secondary: #232a31;
  --secondary-foreground: #fafafa;
  --muted: #5c666b;
  --muted-foreground: #d3d7d9;
  --accent: #062e4f;
  --accent-foreground: #91c8f6;
  --destructive: #ff8888;
  --destructive-foreground: #29313a;
  --border: #8696a9;
  --input: #29313a;
  --ring: #91c8f6;

  /* Chart Colors - SAP Fiori 3 Dark Palette */
  --chart-1: #91c8f6;
  --chart-2: #abe2ab;
  --chart-3: #fabd64;
  --chart-4: #ff8888;
  --chart-5: #b995e0;

  /* Sidebar/Shell Colors - Dark */
  --sidebar: #354a5f;
  --sidebar-foreground: #fafafa;
  --sidebar-primary: #d1e8ff;
  --sidebar-primary-foreground: #354a5f;
  --sidebar-accent: #283848;
  --sidebar-accent-foreground: #fafafa;
  --sidebar-border: #7996b4;
  --sidebar-ring: #d1e8ff;

  /* Typography - Same fonts */
  --font-sans: 'SAP72', ui-sans-serif, system-ui, -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, 'Helvetica Neue', Arial, sans-serif;
  --font-serif: ui-serif, Georgia, Cambria, "Times New Roman", Times, serif;
  --font-mono: '72Mono', ui-monospace, SFMono-Regular, Menlo, Monaco, Consolas, "Liberation Mono", "Courier New", monospace;

  /* Border Radius - Same */
  --radius: 0.25rem;

  /* Shadows - SAP Fiori 3 Dark Shadow System */
  --shadow-2xs: 0 0 0 0.0625rem rgba(255, 255, 255, 0.2);
  --shadow-xs: 0 0 0 0.0625rem rgba(255, 255, 255, 0.2), 0 0.125rem 0.5rem 0 rgba(0, 0, 0, 0.3);
  --shadow-sm: 0 0 0 0.0625rem rgba(255, 255, 255, 0.2), 0 0.125rem 0.5rem 0 rgba(0, 0, 0, 0.3);
  --shadow: 0 0 0 0.0625rem rgba(255, 255, 255, 0.2), 0 0.125rem 0.5rem 0 rgba(0, 0, 0, 0.3);
  --shadow-md: 0 0 0 0.0625rem rgba(255, 255, 255, 0.5), 0 0.125rem 0.5rem 0 rgba(0, 0, 0, 0.6);
  --shadow-lg: 0 0 0 0.0625rem rgba(255, 255, 255, 0.5), 0 0.625rem 1.875rem 0 rgba(0, 0, 0, 0.6);
  --shadow-xl: 0 0 0 0.0625rem rgba(255, 255, 255, 0.5), 0 1.25rem 5rem 0 rgba(0, 0, 0, 0.6);
  --shadow-2xl: 0 0.125rem 0.125rem 0 rgba(0, 0, 0, 0.2), inset 0 -0.0625rem 0 0 #3a4552;

  /* SAP Specific States - Dark theme (Official) */
  --success: #abe2ab;
  --success-foreground: #29313a;
  --warning: #fabd64;
  --warning-foreground: #29313a;
  --information: #91c8f6;
  --information-foreground: #29313a;
  --neutral: #d3d7d9;
  --neutral-foreground: #29313a;

  /* SAP State Backgrounds - Dark theme (Official) */
  --success-background: #153c15;
  --warning-background: #2b1a01;
  --error-background: #3b0000;
  --information-background: #062e4f;
  --neutral-background: #5c666b;
}

/* Base Styles */
* {
  box-sizing: border-box;
}

body {
  font-family: var(--font-sans);
  font-size: 0.875rem;
  line-height: 1.5;
  color: var(--foreground);
  background-color: var(--background);
  margin: 0;
  -webkit-font-smoothing: antialiased;
  -moz-osx-font-smoothing: grayscale;
}

import { type ReactNode } from "react";

import { BrandHeader } from "@/components/brand-header";

import { cn } from "@/lib/utils";

import "@/app/globals.css";
import "@/app/tokens.css";

export default function ShellLayout({
  children,
}: Readonly<{
  children: ReactNode;
}>) {
  return (
    <html
      lang="en"
      className={cn("font-sans bg-background text-foreground")}
    >
      <body>
        <BrandHeader />
        <main className="mt-16 flex w-full justify-center">
          <div className="container">{children}</div>
        </main>
      </body>
    </html>
  );
}
