"use client";

import { Logo } from "@/components/logo";

import { zodResolver } from "@hookform/resolvers/zod";
import Link from "next/link";
import * as React from "react";
import { useForm } from "react-hook-form";
import * as z from "zod";

import { Button } from "@/components/ui/button";
import {
  Form,
  FormControl,
  FormField,
  FormItem,
  FormLabel,
  FormMessage,
} from "@/components/ui/form";
import { Input } from "@/components/ui/input";
import { Separator } from "@/components/ui/separator";

const formSchema = z.object({
  email: z.string().email({
    message: "Please enter a valid email address.",
  }),
  password: z.string().min(8, {
    message: "Password must be at least 8 characters.",
  }),
});

function LoginForm() {
  const [isLoading, setIsLoading] = React.useState<boolean>(false);

  const form = useForm<z.infer<typeof formSchema>>({
    resolver: zodResolver(formSchema),
    defaultValues: {
      email: "",
      password: "",
    },
  });

  async function onSubmit(values: z.infer<typeof formSchema>) {
    setIsLoading(true);
  }

  return (
    <div className="grid gap-6">
      <Form {...form}>
        <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
          <FormField
            control={form.control}
            name="email"
            render={({ field }) => (
              <FormItem>
                <FormLabel>Email</FormLabel>
                <FormControl>
                  <Input placeholder="name@example.com" {...field} />
                </FormControl>
                <FormMessage />
              </FormItem>
            )}
          />
          <FormField
            control={form.control}
            name="password"
            render={({ field }) => (
              <FormItem>
                <div className="flex items-center justify-between">
                  <FormLabel>Password</FormLabel>
                  <Link
                    href="#forgot-password"
                    className="text-muted-foreground text-xs hover:text-primary"
                  >
                    Forgot password?
                  </Link>
                </div>
                <FormControl>
                  <Input type="password" {...field} />
                </FormControl>
                <FormMessage />
              </FormItem>
            )}
          />
          <Button type="submit" className="w-full" disabled={isLoading}>
            {isLoading ? "Signing in..." : "Sign in"}
          </Button>
        </form>
      </Form>

      <div className="relative">
        <div className="absolute inset-0 flex items-center">
          <Separator className="w-full" />
        </div>
        <div className="relative flex justify-center text-xs uppercase">
          <span className="bg-background px-2 text-muted-foreground">
            Or continue with
          </span>
        </div>
      </div>

      <div className="grid grid-cols-2 gap-4">
        <Button variant="outline" type="button" disabled={isLoading}>
          Google
        </Button>
        <Button variant="outline" type="button" disabled={isLoading}>
          Microsoft
        </Button>
      </div>

      <div className="text-center text-sm">
        Don&apos;t have an account?{" "}
        <Link href="/signup" className="text-primary hover:underline">
          Sign up
        </Link>
      </div>
    </div>
  );
}

export function Login() {
  return (
    <div className="container relative flex-col items-center justify-center md:grid lg:max-w-none lg:grid-cols-2 lg:px-0">
      <div className="relative hidden h-full flex-col bg-foreground/10 p-10 text-foreground lg:flex dark:border-r">
        <div className="relative z-20 flex items-center font-medium text-lg">
          <Logo />
        </div>

        <div className="relative z-20 mt-auto">
          <blockquote className="space-y-2">
            <p className="text-lg">
              &ldquo;Tbh @shadcn really cooked with @shadcn. Keeps passing the
              test of time.&rdquo;
            </p>
            <footer className="text-sm">Guillermo Rauch, CEO of Vercel</footer>
          </blockquote>
        </div>
      </div>

      <div className="lg:p-8">
        <div className="mx-auto flex w-full flex-col justify-center space-y-6 sm:w-[350px]">
          <div className="flex flex-col space-y-2 text-center">
            <h1 className="font-semibold text-2xl tracking-tight">
              Welcome back
            </h1>
            <p className="text-muted-foreground text-sm">
              Enter your email to sign in to your account
            </p>
          </div>

          <LoginForm />

          <p className="px-8 text-center text-muted-foreground text-sm">
            By clicking continue, you agree to our{" "}
            <Link
              href="#terms"
              className="underline underline-offset-4 hover:text-primary"
            >
              Terms of Service
            </Link>{" "}
            and{" "}
            <Link
              href="#privacy"
              className="underline underline-offset-4 hover:text-primary"
            >
              Privacy Policy
            </Link>
            .
          </p>
        </div>
      </div>
    </div>
  );
}

import { type ReactNode } from "react";

import { cn } from "@/lib/utils";

import "@/app/globals.css";
import "@/app/tokens.css";

export default function RootLayout({
  children,
}: Readonly<{
  children: ReactNode;
}>) {
  return (
    <html
      lang="en"
      className={cn("font-sans bg-background text-foreground")}
    >
      <body>
        <main className="mt-16 flex w-full justify-center">
          <div className="container">{children}</div>
        </main>
      </body>
    </html>
  );
}

"use client";

import { useState } from "react";
import { Button } from "@/components/ui/button";
import {
  MessageBox,
  type MessageBoxAction,
  type MessageBoxType,
} from "../../../../components/message-box";

export default function MessageBoxDemo() {
  const [isOpen, setIsOpen] = useState(false);
  const [messageBoxType, setMessageBoxType] =
    useState<MessageBoxType>("Confirm");
  const [lastAction, setLastAction] = useState<MessageBoxAction | null>(null);

  const handleAction = (action: MessageBoxAction) => {
    setLastAction(action);
    console.log("[v0] MessageBox action:", action);
  };

  const showConfirmation = () => {
    setMessageBoxType("Confirm");
    setIsOpen(true);
  };

  const showInformation = () => {
    setMessageBoxType("Information");
    setIsOpen(true);
  };

  const showSuccess = () => {
    setMessageBoxType("Success");
    setIsOpen(true);
  };

  const showWarning = () => {
    setMessageBoxType("Warning");
    setIsOpen(true);
  };

  const showError = () => {
    setMessageBoxType("Error");
    setIsOpen(true);
  };

  return (
    <div className="flex flex-col items-center justify-center gap-6 py-12">
      <div className="text-center space-y-2">
        <h1 className="text-3xl font-bold">MessageBox Component Demo</h1>
        <p className="text-muted-foreground">
          UI5-compatible MessageBox built with shadcn/ui and Tailwind CSS
        </p>
      </div>

      <div className="grid grid-cols-2 md:grid-cols-3 gap-4">
        <Button onClick={showConfirmation} variant="default">
          Show Confirmation
        </Button>
        <Button onClick={showInformation} variant="secondary">
          Show Information
        </Button>
        <Button onClick={showSuccess} variant="outline">
          Show Success
        </Button>
        <Button onClick={showWarning} variant="outline">
          Show Warning
        </Button>
        <Button onClick={showError} variant="destructive">
          Show Error
        </Button>
      </div>

      {lastAction && (
        <div className="text-center p-4 bg-muted rounded-lg">
          <p className="text-sm text-muted-foreground">Last action:</p>
          <p className="font-semibold">{lastAction}</p>
        </div>
      )}

      <MessageBox
        open={isOpen}
        onOpenChange={setIsOpen}
        type={messageBoxType}
        title={
          messageBoxType === "Confirm"
            ? "BestÃ¤tigung"
            : messageBoxType === "Information"
            ? "Information"
            : messageBoxType === "Success"
            ? "Success"
            : messageBoxType === "Warning"
            ? "Warning"
            : "Error"
        }
        onAction={handleAction}
        emphasizedAction="OK"
      >
        {messageBoxType === "Confirm" &&
          'You can close the MessageBox by pressing "Escape" or selecting one of the footer buttons.'}
        {messageBoxType === "Information" &&
          "This is an informational message to provide you with important details."}
        {messageBoxType === "Success" &&
          "The operation completed successfully!"}
        {messageBoxType === "Warning" &&
          "Please review your input before proceeding."}
        {messageBoxType === "Error" &&
          "An error occurred while processing your request."}
      </MessageBox>
    </div>
  );
}
