"use client";

import { useState } from "react";
import { Button } from "@/components/ui/button";
import {
  MessageBox,
  type MessageBoxAction,
  type MessageBoxType,
} from "../../../../components/message-box";

export default function MessageBoxDemo() {
  const [isOpen, setIsOpen] = useState(false);
  const [messageBoxType, setMessageBoxType] =
    useState<MessageBoxType>("Confirm");
  const [lastAction, setLastAction] = useState<MessageBoxAction | null>(null);

  const handleAction = (action: MessageBoxAction) => {
    setLastAction(action);
    console.log("[v0] MessageBox action:", action);
  };

  const showConfirmation = () => {
    setMessageBoxType("Confirm");
    setIsOpen(true);
  };

  const showInformation = () => {
    setMessageBoxType("Information");
    setIsOpen(true);
  };

  const showSuccess = () => {
    setMessageBoxType("Success");
    setIsOpen(true);
  };

  const showWarning = () => {
    setMessageBoxType("Warning");
    setIsOpen(true);
  };

  const showError = () => {
    setMessageBoxType("Error");
    setIsOpen(true);
  };

  return (
    <div className="flex flex-col items-center justify-center gap-6 py-12">
      <div className="text-center space-y-2">
        <h1 className="text-3xl font-bold">MessageBox Component Demo</h1>
        <p className="text-muted-foreground">
          UI5-compatible MessageBox built with shadcn/ui and Tailwind CSS
        </p>
      </div>

      <div className="grid grid-cols-2 md:grid-cols-3 gap-4">
        <Button onClick={showConfirmation} variant="default">
          Show Confirmation
        </Button>
        <Button onClick={showInformation} variant="secondary">
          Show Information
        </Button>
        <Button onClick={showSuccess} variant="outline">
          Show Success
        </Button>
        <Button onClick={showWarning} variant="outline">
          Show Warning
        </Button>
        <Button onClick={showError} variant="destructive">
          Show Error
        </Button>
      </div>

      {lastAction && (
        <div className="text-center p-4 bg-muted rounded-lg">
          <p className="text-sm text-muted-foreground">Last action:</p>
          <p className="font-semibold">{lastAction}</p>
        </div>
      )}

      <MessageBox
        open={isOpen}
        onOpenChange={setIsOpen}
        type={messageBoxType}
        title={
          messageBoxType === "Confirm"
            ? "BestÃ¤tigung"
            : messageBoxType === "Information"
            ? "Information"
            : messageBoxType === "Success"
            ? "Success"
            : messageBoxType === "Warning"
            ? "Warning"
            : "Error"
        }
        onAction={handleAction}
        emphasizedAction="OK"
      >
        {messageBoxType === "Confirm" &&
          'You can close the MessageBox by pressing "Escape" or selecting one of the footer buttons.'}
        {messageBoxType === "Information" &&
          "This is an informational message to provide you with important details."}
        {messageBoxType === "Success" &&
          "The operation completed successfully!"}
        {messageBoxType === "Warning" &&
          "Please review your input before proceeding."}
        {messageBoxType === "Error" &&
          "An error occurred while processing your request."}
      </MessageBox>
    </div>
  );
}
